import React, {  useState } from 'react'
import EmojiPicker from 'emoji-picker-react';
import { AddReactionOutlined } from '@mui/icons-material';
import { Mention } from 'react-mentions';

const Input = (props) => {
  const [inputText, setInputText] = useState("");
  // const [items, setItems] = useState([]);
  const [value,setValue] = useState('');
  const [showPicker, setShowPicker] = useState(false);

  

  const handleChange = (event) => {
    event.preventDefault();
    setInputText(event.target.value);
    setInputText("");
  }

  
  const handleEmojiClick = (event, emojiObject) => {
    console.log(emojiObject);
  }



  

  // function handleChange() {
  //   setInputText(prevItems => {
  //     return [...prevItems, inputText];
  //   });
  //   setInputText("");
  // }

  
  const handleChangeMention = (event) =>{
      setValue(event.target.value);
  }

  const handleSubmit = (event) =>{
      event.preventDefault();

      setValue(prevValue => {
        return [...prevValue, value];
      });
      setValue("");
    
  }

//   const handleText = (event) =>{
//     event.preventDefault();

    
//     setInputText(prevItems => {
//       return [...prevItems, inputText];
//     });
//     setInputText("");
  
// }

  const handleTextInputBlur = (e) => {
    setShowPicker(false);
  }


  
  
  
  
  return (
    // <form className='input' >
    //   <input
    //     type="text"
    //     placeholder="Type something..."   
    //     // onChange={handleChange}
    //     onChange={(e) => setInputText(e.target.value)}

    //     value={inputText}
    //   />
      
    //   <div className="send">
       
      
    //     <button onClick={addItem}>
    //       {/* send */}
    //     <AddReactionOutlined/>
    //     </button>

    //   </div>
    // </form>
    // onSubmit={handleSubmit}

    <form className='input' onSubmit={handleSubmit}>
        <input
        placeholder="Type something."
        type = "text"
        value={inputText}
         onChange={(event) => {
          event.preventDefault();

          setInputText(event.target.value)}
          }

        // onChange={(e) => setInputText(e.target.value)}
        // value={inputText}
        // onChange={handleText}
        onBlur={handleTextInputBlur}
        />

      <Mention
      value={value}
      onChange={handleChangeMention}
      trigger={"@"}
      data={[{ id: 1, display: 'John Doe' }, { id: 2, display: 'Jane Doe' }]}
        />

        {/* <button onClick={setValue}>
        <AddReactionOutlined/>
        </button> */}
        {/* <button type='submit' className='send' onClick={setValue} >
        <AddReactionOutlined/>

        </button> */}

<button onClick={() => setShowPicker(!showPicker)}>
<AddReactionOutlined/>

      </button>
      {showPicker && (
        <div className="emoji-picker-container">
          <EmojiPicker onEmojiClick={handleEmojiClick} />
        </div>
      )}
    </form>

    
  )
}

export default Input
