    //get current time 
  const date = new Date();
    const showTime = date.getHours() 
        + ':' + date.getMinutes() ;
        
  //given user_list
  const user_list = ["Alan", "Bob", "Carol", "Dean", "Elin"]

  //get random number between 0 to 4
  function randomIntFromInterval(min, max) { // min and max included 
    return Math.floor(Math.random() * (max - min + 1) + min)
  }
  
  const rndInt = randomIntFromInterval(0, 4)
  // find first name from list
  const userName = user_list[rndInt];

  // get first letter from the userName
  const getName = userName.charAt(0);

  return (
    <div className='message'>
      

      <div className="messageInfo">
      <div class="message-circle">{getName}</div>
      </div>

      
      <div>
      <div className='nameTime'>
      <div className="name">{ user_list[rndInt]}</div>
        <div className="time">{showTime}</div>
      </div>
      <div className="messageContent">
         <p>{props.inputValue}</p>

         {/* <div>
         {items.map(todoItem => (
            <li>{todoItem}</li>
          ))}
         </div> */}
         
        </div>
      </div>


        
      


       

        
     
    </div>
  )
}

export default Message
