import React, {  useState } from 'react'
import EmojiPicker from 'emoji-picker-react';
import { AddReactionOutlined } from '@mui/icons-material';
import { Mention } from 'react-mentions';

const Input = (props) => {
  const [inputText, setInputText] = useState("");
  const [value,setValue] = useState('');
  const [showPicker, setShowPicker] = useState(false);

  // const [text, setText] = useState("");



  // const handleChange = (event) => {
  //   setInputValue(event.target.value);
  // }

  // function handleChange(event) {
  //   const newValue = event.target.value;
  //   setInputText(newValue);
  // }

  

  const handleEmojiClick = (event, emojiObject) => {
    console.log(emojiObject);
  }

  // useEffect(() => {
  //   const handleClick = (event) => {
  //     if (!event.target.closest('.emoji-picker-container')) {
  //       setShowPicker(false);
  //     }
  //   }
  //   document.body.addEventListener('click', handleClick);
  //   return () => {
  //     document.body.removeEventListener('click', handleClick);
  //   };
  // }, [setShowPicker]);

  

  function addItem() {
    setItems(prevItems => {
      return [...prevItems, inputText];
    });
    setInputText("");
  }

  // function handleChange(event) {
  //   const inputValue = event.target.value;
  //   console.log(inputValue);
  //   }

  const handleChangeMention = (event) =>{
      setValue(event.target.value);
  }

  const handleSubmit = (event) =>{
      event.preventDefault();

      // setValue(value);
      setValue(prevItems => {
        return [...prevItems, value];
      });
      setValue("");
     

  }

  const handleTextInputBlur = (e) => {
    setShowPicker(false);
  }


  
  
  
  
  return (
    // <form className='input' >
    //   <input
    //     type="text"
    //     placeholder="Type something..."   
    //     // onChange={handleChange}
    //     onChange={(e) => setInputText(e.target.value)}

    //     value={inputText}
    //   />
      
    //   <div className="send">
       
      
    //     <button onClick={addItem}>
    //       {/* send */}
    //     <AddReactionOutlined/>
    //     </button>

    //   </div>
    // </form>
    // onSubmit={handleSubmit}
    <form className='input' onSubmit={handleSubmit}>
        <input
        placeholder="Type something."
        type = "text"
        value={inputText}
         onChange={(event) => {
          event.preventDefault();

          setInputText(event.target.value)}
          }

        // onChange={(e) => setText(e.target.value)}
        // value={text}
        // onChange={handleChange}
        onBlur={handleTextInputBlur}
        />

      <Mention
      value={value}
      onChange={handleChangeMention}
      trigger={"@"}
      data={[{ id: 1, display: 'John Doe' }, { id: 2, display: 'Jane Doe' }]}
        />

        {/* <button onClick={setValue}>
        <AddReactionOutlined/>
        </button> */}
        {/* <button type='submit' className='send' onClick={setValue} >
        <AddReactionOutlined/>

        </button> */}

<button onClick={() => setShowPicker(!showPicker)}>
<AddReactionOutlined/>

      </button>
      {showPicker && (
        <div className="emoji-picker-container">
          <EmojiPicker onEmojiClick={handleEmojiClick} />
        </div>
      )}
    </form>

    
  )
}

export default Input
